import{_ as i,c as l,a as e,o as n}from"./app-CCA8JC_c.js";const s={};function h(r,a){return n(),l("div",null,a[0]||(a[0]=[e('<h1 id="签证diy" tabindex="-1"><a class="header-anchor" href="#签证diy"><span>签证DIY</span></a></h1><h2 id="必备材料" tabindex="-1"><a class="header-anchor" href="#必备材料"><span>必备材料</span></a></h2><h4 id="🔑-必备材料" tabindex="-1"><a class="header-anchor" href="#🔑-必备材料"><span>🔑 必备材料</span></a></h4><ul><li>✅ 成绩单、毕业证、学位证（翻译+公证）</li><li>✅ 护照、学校Offer、雅思成绩单</li><li>✅ 符合要求的电子版照片</li></ul><h4 id="💰-资金证明" tabindex="-1"><a class="header-anchor" href="#💰-资金证明"><span>💰 资金证明</span></a></h4><ul><li>💳 银行流水（覆盖一年）</li><li>🏠 财产证明（股票、房产等）</li><li>💵 存款证明（如果存款充足）</li></ul><h4 id="🛡️-其他支持材料" tabindex="-1"><a class="header-anchor" href="#🛡️-其他支持材料"><span>🛡️ 其他支持材料</span></a></h4><ul><li>🆔 身份证、户口本（翻译）</li><li>🚫 无犯罪证明、工作证明</li><li>🏥 体检报告、驾照</li></ul><h2 id="申请流程" tabindex="-1"><a class="header-anchor" href="#申请流程"><span>申请流程</span></a></h2><h3 id="时间安排" tabindex="-1"><a class="header-anchor" href="#时间安排"><span>时间安排</span></a></h3><p>从雅思成绩下来后开始折腾，25天下签，加上前期准备，应该有一个月左右。</p><h3 id="准备建议" tabindex="-1"><a class="header-anchor" href="#准备建议"><span>准备建议</span></a></h3><ul><li>有些材料准备需要时间，最好提前准备，比如公证文件</li><li>公证和翻译可以找当地的公证局</li><li>体检搞清楚要做什么项目，在哪个医院</li><li>体检可以提前一点准备，新西兰的体检报告是医院提交</li><li>财务证明主要看你能不能支付未来的生活开支</li><li>原则上所有中文材料都需要翻译</li><li>无犯罪证明支付宝上可以申请</li></ul><h3 id="费用预算" tabindex="-1"><a class="header-anchor" href="#费用预算"><span>费用预算</span></a></h3><ul><li>体检：1600元</li><li>公证翻译：约500元</li><li>签证费：约400纽币</li><li>其他杂费：约200元</li></ul><h2 id="注意事项" tabindex="-1"><a class="header-anchor" href="#注意事项"><span>注意事项</span></a></h2><ul><li>以新西兰移民局官网信息为主</li><li>每个人的情况不一样，没有唯一标准</li><li>移民局政策可能更新，不是一成不变的</li><li>别人的情况不一定适合于你</li></ul>',17)]))}const t=i(s,[["render",h]]),c=JSON.parse('{"path":"/preparation/visa-diy.html","title":"签证DIY","lang":"zh-CN","frontmatter":{},"git":{},"filePathRelative":"preparation/visa-diy.md"}');export{t as comp,c as data};
